import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product } from '../models/product.model';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css'],
})
export class ProductDetailsComponent {
  id: string = '';
  product: Product = new Product();

  constructor(
    private actRoute: ActivatedRoute,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    const routeId = this.actRoute.snapshot.paramMap.get('id') ?? '';
    this.id = routeId;
    this.productService.getProductById(this.id).subscribe((response) => {
      console.log(response);
      this.product = response;
      console.log(this.product.imgPath);
    });
  }
}
<mat-card class="card">
  <mat-card-header>
    <mat-card-title> {{ product.name }} </mat-card-title>
    <mat-card-subtitle> Product Category </mat-card-subtitle>
  </mat-card-header>
  <img mat-card-image src="{{ product.imgPath }}" alt="Product Image" />
  <mat-card-actions>
    <button mat-button>View</button>
    <button mat-button>Add to cart</button>
  </mat-card-actions>
</mat-card>
