import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product } from '../models/product.model';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css'],
})
export class ProductDetailsComponent {
  id: string = '';
  currentProduct: Product = new Product();

  constructor(
    private actRoute: ActivatedRoute,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    const routeId = this.actRoute.snapshot.paramMap.get('id') ?? '';
    this.id = routeId;
    this.productService.getProductById(this.id).subscribe((response) => {
      this.currentProduct = response;
      console.log(this.currentProduct);
    });
  }
}
<mat-card class="card">
  <mat-card-header>
    <mat-card-title> {{ currentProduct.name }} </mat-card-title>
    <mat-card-subtitle> {{ currentProduct.category }} </mat-card-subtitle>
  </mat-card-header>
  <img mat-card-image src="{{ currentProduct.imgPath }}" alt="Product Image" />
  <mat-card-content>
    <p>{{ currentProduct.description }}</p>
    <p>Price: {{ currentProduct.price | currency }}</p>
  </mat-card-content>
  <mat-card-actions>
    <button mat-button>Add to cart</button>
  </mat-card-actions>
</mat-card>
